export function calculateCurrent(voltage, diodeType, mode) {
  let Vt, K;

  // Threshold Voltages
  switch (diodeType) {
    case "silicon": Vt = 0.7; break;
    case "germanium": Vt = 0.3; break;
    case "led": Vt = 2.0; break;
    case "zener":
      if (mode === "reverse") {
        // Ideal Zener breakdown
        return (voltage <= -5.6) ? (-0.05 * (voltage + 5.6)) : 0;
      }
      Vt = 0.7; break;
    default: Vt = 0.7;
  }

  if (mode === "reverse") return 0; // Ideal PN reverse = open circuit

  // Ideal but practical slope (sharp rise)
  K = 0.15;

  if (voltage < Vt) return 0;
  return K * (voltage - Vt);
}
