export function calculateCurrent(voltage, diodeType, mode) {
  let Vt, slope;

  switch (diodeType) {
    case "silicon":
      Vt = 0.7; slope = 0.02; break;
    case "germanium":
      Vt = 0.3; slope = 0.015; break;
    case "led":
      Vt = 2.0; slope = 0.01; break;
    case "zener":
      if (mode === "reverse" && voltage <= -5.6) return slope = -0.04 * (voltage + 5.6);
      Vt = 0.7; slope = 0.02; break;
    default:
      Vt = 0.7; slope = 0.02;
  }

  if (mode === "reverse") return 0; // ignore leakage

  if (voltage < Vt) return 0;

  return slope * (voltage - Vt); // Current in A
}
