import { calculateCurrent } from "./calculateCurrent";

export function generateVI(diodeType, mode) {
  let minV, maxV, step;

  if (mode === "forward") {
    if (diodeType === "led") { minV = 0; maxV = 3.5; step = 0.05; }
    else { minV = 0; maxV = 1.2; step = 0.01; }
  } else {
    if (diodeType === "zener") { minV = 0; maxV = -20; step = -0.2; }
    else { minV = 0; maxV = -50; step = -1; }
  }

  let data = [];
  for (let V = minV; mode === "forward" ? V <= maxV : V >= maxV; V += step) {
    data.push({
      voltage: Number(V.toFixed(2)),
      current: Number(calculateCurrent(V, diodeType, mode).toFixed(6))
    });
  }
  return data;
}
