export function calculateCurrent(voltage, diodeType, mode) {
  let Vt, K;

  // ---- Threshold Voltages ----
  switch (diodeType) {
    case "silicon": Vt = 0.7; break;
    case "germanium": Vt = 0.3; break;
    case "led": Vt = 2.0; break;
    case "zener":
      // Zener Reverse Breakdown (3rd Quadrant)
      if (mode === "reverse") {
        let Vz = -5.6;
        if (voltage <= Vz) return -0.15 * (voltage - Vz); // sharp breakdown
        return -0.0002; // small leakage before breakdown
      }
      Vt = 0.7;
      break;
    default:
      Vt = 0.7;
  }

  // ---- Reverse Bias for NON-ZENER diodes → small leakage in 3rd Quadrant ----
  if (mode === "reverse") return -0.0001; // approx -0.1 mA leakage

  // ---- Forward Bias Ideal but Practical Sharp Knee ----
  K = 0.15; // slope steepness

  if (voltage < Vt) return 0; // off
  return K * (voltage - Vt);  // linear rise after knee
}
