import { calculateCurrent } from "./calculateCurrent";

export function generateVI(diodeType, mode) {
  let start, end;

  if (mode === "forward") {
    end = (diodeType === "led") ? 3.5 : 1.2;
    start = 0;
  } else {
    start = 0;
    end = (diodeType === "zener") ? -20 : -50;
  }

  const step = (end - start) / 19; // 20 points
  let data = [];

  for (let i = 0; i < 20; i++) {
    const V = start + step * i;
    data.push({
      voltage: Number(V.toFixed(2)),
      current: Number(calculateCurrent(V, diodeType, mode).toFixed(6))
    });
  }

  return data;
}
