import { calculateCurrent } from "./calculateCurrent";

export function generateVI(diodeType, mode) {
  let start, end;

  if (mode === "forward") {
    switch (diodeType) {
      case "silicon":
        start = 0;
        end = 1.0;   // clearly shows 0.7V knee
        break;

      case "germanium":
        start = 0;
        end = 0.6;   // highlights 0.3V knee clearly
        break;

      case "led":
        start = 0;
        end = 3.5;   // covers 2.0V knee properly
        break;

      case "zener":
        start = 0;
        end = 1.0;   // forward behaves like silicon
        break;

      default:
        start = 0;
        end = 1.0;
    }
  }

  else if (mode === "reverse") {
    switch (diodeType) {
      case "zener":
        start = 0;
        end = -10;   // clearly shows breakdown near -5.6V
        break;

      default:
        start = 0;
        end = -10;   // gives clear small leakage view
    }
  }

  // Always make exactly 20 evenly spaced points
  const step = (end - start) / 19;
  let data = [];

  for (let i = 0; i < 20; i++) {
    const V = start + step * i;
    data.push({
      voltage: Number(V.toFixed(3)),
      current: Number(calculateCurrent(V, diodeType, mode).toFixed(6))
    });
  }

  return data;
}
